mapper.update:
UPDATE info_organization_detail SET
	orgid = #{orgid},
	details = #{details},
	moddate = NOW(),
	userno = #{userno}
WHERE orgid = #{orgid}

mapper.select:
SELECT a.brdid, a.orgid, b.name as orgname, b.orgtypeid, a.title, CASE WHEN b.regdate > CURDATE() THEN DATE_FORMAT(a.regdate, '%H:%i:%s') ELSE DATE_FORMAT(a.regdate, '%Y-%m-%d') END as regdate, c.korname, a.count, (SELECT COUNT(*) FROM brd_orgnization_likes f WHERE a.brdid = f.brdid and f.likes = 1) as likes, (SELECT COUNT(*) FROM brd_orgnization_reply d WHERE d.brdid = a.brdid) as repcount FROM brd_orgnization a LEFT JOIN info_organization b on a.orgid = b.orgid LEFT JOIN info_userinfo c on a.userno = c.userno WHERE b.orgtypeid = #{orgtypeid}
AND a.title LIKE CONCAT('%',#{keyword},'%')
AND c.nickname LIKE CONCAT('%',#{keyword},'%')
ORDER BY a.brdid DESC LIMIT #{skiprow}, #{pagerow}

mapper.update:
UPDATE brd_orgnization SET
	count = count + 1
WHERE brdid = #{brdid}

mapper.select:
SELECT
	id as lid,
	likes
FROM brd_orgnization_likes
WHERE userno = #{userno}
	AND brdid = #{brdid}

mapper.insert:
INSERT INTO brd_orgnization_likes(
	brdid,
	userno,
	likes
)VALUES(
	#{brdid},
	#{userno},
	#{likes}
)

mapper.update:
UPDATE brd_orgnization_likes SET
	likes = likes + 1
WHERE brdid = #{brdid}
	AND userno = #{userno}

UPDATE brd_orgnization_reply_likes SET
	likes = likes + 1
WHERE repid = #{repid}
	AND userno = #{userno}

UPDATE brd_orgnization_reply_likes SET
	dislikes = dislikes + 1
WHERE repid = #{repid}
	AND userno = #{userno}

UPDATE info_userinfo SET
	authid = 2
WHERE userno = #{prno}

UPDATE info_userinfo SET
	authid = 5
WHERE userno = #{oldprno}

UPDATE brd_orgnization_likes SET
	likes = likes - 1
WHERE brdid=#{brdid}
	AND userno =#{userno}

UPDATE brd_orgnization_likes SET
	dislikes = dislikes - 1
WHERE brdid = #{brdid}
	AND userno = #{userno}

UPDATE brd_orgnization_reply_likes SET
	dislikes = dislikes - 1
WHERE repid = #{repid}
	AND userno = #{userno}

UPDATE brd_orgnization_reply_likes SET
	likes = likes - 1
WHERE repid = #{repid}
	AND userno = #{userno}
