mapper.update:
UPDATE brd_extratips SET
	count = count + 1
WHERE brdid = #{brdid}

UPDATE brd_extratips SET
	likes = likes + 1
WHERE brdid = #{brdid}

UPDATE brd_extratips SET
	dislikes = dislikes + 1
WHERE brdid = #{brdid}

UPDATE brd_extratips_reply SET
	likes = likes + 1
WHERE repid = #{repid}

UPDATE brd_extratips_reply SET
	dislikes = dislikes + 1
WHERE repid = #{repid}
