mapper.update:
UPDATE brd_extratips SET
	count = count + 1
WHERE brdid = #{brdid}

UPDATE brd_extratips_likes SET
	likes = likes + 1
WHERE brdid = #{brdid}
	AND userno = #{userno}

UPDATE brd_extratips_likes SET
	dislikes = dislikes + 1
WHERE brdid = #{brdid}
	AND userno = #{userno}

UPDATE brd_extratips_reply_likes SET
	likes = likes + 1
WHERE repid = #{repid}
	AND userno = #{userno}

UPDATE brd_extratips_reply_likes SET
	dislikes = dislikes + 1
WHERE repid = #{repid}
	AND userno = #{userno}

UPDATE brd_extratips SET
	content = #{content},
	title = #{title},
	moddate = NOW(),
	tipid = #{tipid}
WHERE brdid = #{brdid}

mapper.delete:
DELETE FROM brd_extratips_reply
WHERE brdid = #{brdid}

	AND repid = #{repid}

mapper.update:
