	AND userno = #{userno}

mapper.insert:
INSERT INTO brd_extratips( tipid, title, regdate, moddate, content, userno
) VALUES( #{tipid}, #{title}, NOW(), NOW(), #{content}, #{userno}
)
SELECT LAST_INSERT_ID()

